definition.script:
pipeline {
    agent {
        kubernetes {
            cloud 'kubernetes-cluster1'
        	label 'pod-label-cluster1'
        	activeDeadlineSeconds '300'
        	containerTemplate {
                name 'maven-image'
                image 'maven:3.3.9-jdk-8-alpine'
                command 'cat'
		        tty true
        	}
        }
    }
    stages {
        stage('clone git maven') {
            steps {
                container('maven-image') {
			        git url: 'https://github.com/web3j/sample-project-maven.git'
                }
            }
        }
	    stage('Install Maven') {
	        steps {
		        container('maven-image') {
			        sh 'mvn clean install'
			    }
		    }
        }
    }
}
