definition.script:
pipeline {
    agent {
        kubernetes {
            cloud 'kubernetes-cluster1'
            label 'pod-label-cluster1'
            defaultContainer 'jenkins-slave'
            containerTemplate {
                name 'centos-image'
                image 'centos:centos7'
                command 'cat'
                ttyEnabled true
            }
            containerTemplate {
                name 'ubuntu-image'
                image 'ubuntu:bionic'
                command 'cat'
                ttyEnabled true
            }
        }
    }
    stages {
  		stage('Build Centos') {
		    steps {
			    container('ubuntu-image') {
				    sh 'cat /etc/*-release'
			    }
		    }
  		}
      	stage('Build Ubuntu') {
    		steps {
    		    container('ubuntu-image') {
    				sh 'cat /etc/*-release'
    		    }
    		}
      	}
    }
}
