definition.script:
pipeline {
  agent {
    kubernetes {
		cloud 'kubernetes-cluster1'
		label 'pod-label-cluster1'
		defaultContainer 'jenkins-slave '
      yaml """
apiVersion: v1
kind: Pod
metadata:
  labels:
    os: centos7
spec:
  containers:
  - name: centos-image 
    image: centos:centos7
    command:
    - cat
    tty: true
  - name: ubuntu-image 
    image: ubuntu:bionic
    command:
    - cat
    tty: true
"""
    }
  }
  stages {
	stage('Build centos') {
		steps {
			container('centos-image') {
				sh 'cat /etc/*-release'
			}
		}
	}
	stage('Build ubuntu') {
	    steps {
		    container('ubuntu-image') {
	            sh 'cat /etc/*-release'
		    }
	    }
	}
  }
}
