definition.script:
podTemplate(cloud: 'kubernetes-cluster1', containers: [containerTemplate(args: 'cat', command: '/bin/sh -c', image: 'ubuntu:bionic', name: 'ubuntu-image', ttyEnabled: true), containerTemplate(args: 'cat', command: '/bin/sh -c', image: 'centos:centos7', name: 'centos-image', ttyEnabled: true)], label: 'pod-label-cluster1', name: 'pod-label-cluster1') {
      node('pod-label-cluster1') {
		stage('Check Ubuntu Release') {
			container('ubuntu-image') {
			  sh "cat /etc/*-release"
				}
		  }
		stage('Check Centos Release') {
			container('centos-image') {
			  sh "cat /etc/*-release"
				}
		  }
	}
}
